CREATE TABLE `resule_info`  (
  `result_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '成果编号',
  `result_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '成果名称',
  `result_value` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '成果内容',
  `result_imge` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '图片地址',
  `result_link` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '成果地址',
  `result_time` datetime(0) NULL DEFAULT NULL COMMENT '成果时间',
  `user_stid` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '学号',
  `type_id` int(11) NOT NULL COMMENT '所属分类编号',
  PRIMARY KEY (`result_id`) USING BTREE,
  UNIQUE INDEX `result_id`(`result_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 26 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '成果' ROW_FORMAT = Compact;

